import { Component, OnInit } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { ActivatedRoute } from '@angular/router';
import { MainService } from '../main.service';

@Component({
  selector: 'app-edit-data',
  templateUrl: './edit-data.component.html',
  styleUrls: ['./edit-data.component.css']
})
export class EditDataComponent implements OnInit {

  signupform!: FormGroup;

  roles=['Admin','SuperAdmin','Subscriber'];

  keys=['Admin','SuperAdmin','Subscriber'];
  id:any;
  private myData:any;
  middle_name:any;

  constructor(private _Activatedroute:ActivatedRoute,private mainService:MainService) { }

  async ngOnInit() {

 this.id=this._Activatedroute.snapshot.paramMap.get("id");

 await this.mainService.getUserById(this.id).subscribe((res:any) => {

  this.myData = res;
  this.middle_name=this.myData[0]['middle_name'];

});
console.log(this.middle_name);

     this.signupform=new FormGroup(
       {
         'uid':new FormControl(this.id),
         'fname':new FormControl("faiz",[Validators.required]),
         'mname':new FormControl(this.middle_name,[Validators.required]),
         'lname':new FormControl('Alam',[Validators.required]),
         'email':new FormControl('f@gmail.com',[Validators.required,Validators.email]),
         'phonenumber':new FormControl('994443234',[Validators.required]),
         'role':new FormControl('SuperAdmin',[Validators.required]),
         'address':new FormControl('Sup',[Validators.required]),
         'cname':new FormControl('Sf',[Validators.required]),
         'website':new FormControl('sf.com',[Validators.required]),
         'caddress':new FormControl('Sup',[Validators.required]),
         'rname':new FormControl('SuperAdmin',[Validators.required]),
         'key':new FormControl('SuperAdmin',[Validators.required]),
         'description':new FormControl('No access',[Validators.required])
       }
     );

  }

  // onSubmit(): void {
  //   this.addUser()
  //   alert('Submitted!');


  // }

  onSubmit(user:any){
    console.log(user);

     this.mainService.updateUser(user).subscribe((res:any) => {

     });
     alert("updated");    
  }


  cancel(){
    this.ngOnInit();

  }

}
<div class="form-group">

<form [formGroup]='signupform' (ngSubmit)="onSubmit(signupform.value)">


  <div class="form-group1">
    <label for="uid">User Id:</label>
    <input id="uid" type="text" name="uid" formControlName="uid">

</div>

  <div class="form-group1">
    <label for="fname">First Name:</label>
    <input id="fname" type="text" name="fname" formControlName="fname">

</div>

<div class="form-group1">
    <label for="mname">Middle Name:</label>
    <input id="mname" type="text" name="mname" formControlName="mname">
    <span class="warning-block" *ngIf="!signupform.get('mname')!.valid && signupform.get('mname')!.touched">Please enter valid Middle Name</span>
  </div>


<div class="form-group1">
    <label for="lname">Last Name:</label>
    <input id="lname" type="text" name="lname" formControlName="lname">
    <span class="warning-block" *ngIf="!signupform.get('lname')!.valid && signupform.get('lname')!.touched">Please enter valid Last Name</span>

</div>

<div class="form-group1">
    <label for="email">Email:</label>
    <input id="email" type="text" name="email" formControlName="email">
    <span class="warning-block" *ngIf="!signupform.get('email')!.valid && signupform.get('email')!.touched">Please enter valid Email</span>
</div>

<div class="form-group1">

    <label for="phonenumber">Phone Number:</label>
    <input id="phonenumber" type="text" name="phonenumber" formControlName="phonenumber">
    <span class="warning-block" *ngIf="!signupform.get('phonenumber')!.valid && signupform.get('phonenumber')!.touched">Please enter valid Phone Number</span>

    </div>



    <label for="role">Role:</label>
    <select class="form-control" id="role" formControlName="role" required>
      <option *ngFor="let role of roles" [value]="role" name="role" >{{role}}</option>
    </select>




    <div class="form-group1">

    <label for="address">Address:</label>
    <input id="address" type="text" name="address" formControlName="address">
    <span class="warning-block" *ngIf="!signupform.get('address')!.valid && signupform.get('address')!.touched">Please enter valid Address</span>

    </div>

    <div class="form-group1">

      <label for="cname">Customer Name:</label>
      <input id="cname" type="text" name="cname" formControlName="cname">
      <span class="warning-block" *ngIf="!signupform.get('cname')!.valid && signupform.get('cname')!.touched">Please enter valid customer name</span>

      </div>

      <div class="form-group1">

        <label for="website">Website:</label>
        <input id="website" type="text" name="website" formControlName="website">
        <span class="warning-block" *ngIf="!signupform.get('website')!.valid && signupform.get('website')!.touched">Please enter valid website</span>

        </div>

        <div class="form-group1">

          <label for="caddress">Customer Address:</label>
          <input id="caddress" type="text" name="caddress" formControlName="caddress">
          <span class="warning-block" *ngIf="!signupform.get('caddress')!.valid && signupform.get('caddress')!.touched">Please enter valid customer address</span>

          </div>

          <div class="form-group1">

            <label for="rname">Role Name:</label>
            <input id="rname" type="text" name="rname" formControlName="rname">
            <span class="warning-block" *ngIf="!signupform.get('rname')!.valid && signupform.get('rname')!.touched">Please enter role name</span>

            </div>

            <label for="key">Key:</label>
            <select class="form-control" id="key" formControlName="key" required>
              <option *ngFor="let key of keys" [value]="key" name="key" >{{key}}</option>
            </select>

            <div class="form-group1">

              <label for="description">Description:</label>
              <input id="description" type="text" name="description" formControlName="description">

              </div>

    <div class="form-group1">

    <input type="Submit" value="Save" [disabled]="!signupform.valid" >
    <input type="button" id="cancel" value="Cancel" (click)="cancel()">
    </div>


</form>


  </div>
